import React, { Component } from 'react';
import './App.css';
import { Route, Switch, withRouter } from 'react-router-dom';
import PersonalInfo from './containers/infos/personalInfo/personalInfo';
import Preview from './components/preview/preview';
import Skills from './containers/infos/skills/skills';
import Educations from './containers/infos/educations/educations';
import Projects from './containers/infos/projects/projects';
import Extras from './containers/infos/extras/extras';
import Auth from './containers/Auth/auth';
import axios from 'axios';
import Print from './components/print/print';
import Error from './UI/error/error';
import Signup from './containers/Auth/signup/signup';
var url ="http://192.168.43.127:5000";
class App extends Component {

  state={
    controls: {
      email: {
          elementType: 'input',
          elementConfig: {
              type: 'text',
              placeholder: 'Your Email'
          },
          value: '',
          validation: {
              isRequired: true
          },
          valid: false,
          touched: false
      },
      password: {
          elementType: 'input',
          elementConfig: {
              type: 'password',
              placeholder: 'password'
          },
          value: '',
          validation: {
              isRequired: true,
              minLength: 6
          },
          valid: false,
          touched: false
      }
  },
  controlSignUp: {
    email: {
        elementType: 'input',
        elementConfig: {
            type: 'text',
            placeholder: 'Your Email'
        },
        value: '',
        validation: {
            isRequired: true
        },
        valid: false,
        touched: false
    },
    password: {
        elementType: 'input',
        elementConfig: {
            type: 'password',
            placeholder: 'password'
        },
        value: '',
        validation: {
            isRequired: true,
            minLength: 6
        },
        valid: false,
        touched: false
    }
},
    personalInfo: {
      name:{
        elementType: 'input',
        elementConfig: {
            type: 'text',
            placeholder: 'Your Name'
        },
        value: '',
        validation: {
            isRequired: true
        },
        valid: false,
        touched: false
    },
      email: {
        elementType: 'input',
        elementConfig: {
            type: 'text',
            placeholder: 'Your email'
        },
        value: '',
        validation: {
            isRequired: true
        },
        valid: false,
        touched: false
    },
      phone: {
        elementType: 'input',
        elementConfig: {
            type: 'text',
            placeholder: 'phone'
        },
        value: '',
        validation: {
            isRequired: true
        },
        valid: false,
        touched: false
    }
  },
  skills: [{
      skill: {
        elementType: 'input',
        elementConfig: {
            type: 'text',
            placeholder: 'Your skills'
        },
        value: '',
        id: '0',
        validation: {
            isRequired: true
        },
        valid: false,
        touched: false
      },
    }],
    educations: [{
      education: {
        elementType: 'text',
        elementConfig: {
            type: 'text',
            placeholder: 'education',
        },
        value: '',
        std: '',
        details: '',
        id: '0',
        validation: {
            isRequired: true
        },
        valid: false,
        touched: false
      }
    }],
    projects: [{
      project: {
        elementType: 'input',
        elementConfig: {
            type: 'text',
            placeholder: 'projects',
        },
        value: '',
        desc: '',
        id: '0',
        validation: {
            isRequired: true
        },
        valid: false,
        touched: false
      },
    }],
    extras: [{
      extra: {
        elementType: 'input',
        elementConfig: {
            type: 'text',
            placeholder: 'extra details',
        },
        value: '',
        id: '0',
        validation: {
            isRequired: true
        },
        valid: false,
        touched: false
      },
    }],
    flash: ""
  }

  onChangeHandler=(event, id)=>{
    const info = {...this.state.personalInfo};
    const data = {...info[id]};
    data.value = event.target.value;
    info[id] = data;
    this.setState({ personalInfo: info });
  }

  onChangeSkillHandler(event, id){
    const info = [...this.state.skills];

    let changedInfo = info.map((s)=>{
      if(s.skill.id===id){
        s.skill.value = event.target.value;
      }
      return s;
    })

    this.setState({skills: changedInfo});
  }

  addHandler=(numSkills)=>{
    const info = [...this.state.skills];
    console.log(numSkills);
    info.push({
      skill: {
        elementType: 'input',
        elementConfig: {
            type: 'text',
